using System.Drawing.Imaging;

class Program
{
    static void Main()
    {
        // Base64 image string make sure it does not contain any data or base code in beganning 
        string base64Image = "";

       
        byte[] imageBytes = Convert.FromBase64String(base64Image);

        using (MemoryStream ms = new MemoryStream(imageBytes))
        {
            System.Drawing.Image image = System.Drawing.Image.FromStream(ms);

            // Update the meta description
            UpdateMetaDescription(image, "New meta description");

            string outputPath = "D:/New folder/MetaDataUpdater/test.jpeg";
            SaveImage(image, outputPath);

            Console.WriteLine("Image saved.");
        }
    }

    static void UpdateMetaDescription(System.Drawing.Image image, string metaDescription)
    {
        PropertyItem propItem = image.PropertyItems[0];

        //This is property code 
        propItem.Id = 0x010E;

        byte[] descriptionBytes = System.Text.Encoding.ASCII.GetBytes(metaDescription);

        // Set the property type and value
        propItem.Type = 2; // ASCII string type
        propItem.Len = descriptionBytes.Length;
        propItem.Value = descriptionBytes;

        image.SetPropertyItem(propItem);
    }

    static void SaveImage(System.Drawing.Image image, string outputPath)
    {
        
        image.Save(outputPath, ImageFormat.Jpeg);
    }
}
